const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <div {...{
      "className": "admonition admonition-note alert alert--secondary"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "14",
              "height": "16",
              "viewBox": "0 0 14 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M6.3 5.69a.942.942 0 0 1-.28-.7c0-.28.09-.52.28-.7.19-.18.42-.28.7-.28.28 0 .52.09.7.28.18.19.28.42.28.7 0 .28-.09.52-.28.7a1 1 0 0 1-.7.3c-.28 0-.52-.11-.7-.3zM8 7.99c-.02-.25-.11-.48-.31-.69-.2-.19-.42-.3-.69-.31H6c-.27.02-.48.13-.69.31-.2.2-.3.44-.31.69h1v3c.02.27.11.5.31.69.2.2.42.31.69.31h1c.27 0 .48-.11.69-.31.2-.19.3-.42.31-.69H8V7.98v.01zM7 2.3c-3.14 0-5.7 2.54-5.7 5.68 0 3.14 2.56 5.7 5.7 5.7s5.7-2.55 5.7-5.7c0-3.15-2.56-5.69-5.7-5.69v.01zM7 .98c3.86 0 7 3.14 7 7s-3.14 7-7 7-7-3.12-7-7 3.14-7 7-7z"
              }}></path></svg></span>{`Public Preview`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}><p parentName="div">{`LiveData Migrator is in public preview. This gives you access to all product functionality for review, but limits operation time to 10 minutes during the preview period.`}</p></div></div>
    <p>{`Find a comprehensive description of each command available from the LiveData Migrator action prompt here. Review the `}<a parentName="p" {...{
        "href": "/live-data-migrator/docs/operation"
      }}>{`LiveData Migrator Operation`}</a>{` guide for information on how to use the commands to perform migration.`}</p>
    <p>{`Each command description below includes the information available from the action prompt using the `}<inlineCode parentName="p">{`help`}</inlineCode>{` command. Tab-completion will also give you guidance when entering commands on the available options and to auto-complete values needed.`}</p>
    <h2 {...{
      "id": "built-in-commands"
    }}>{`Built-in Commands`}</h2>
    <hr></hr>
    <h3 {...{
      "id": "clear"
    }}><inlineCode parentName="h3">{`clear`}</inlineCode></h3>
    <p>{`Clear the interactive action prompt screen output with the `}<inlineCode parentName="p">{`clear`}</inlineCode>{` command. You can also type `}<inlineCode parentName="p">{`<Ctrl-L>`}</inlineCode>{` to achieve the same, even while typing another command.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text",
        "metastring": "title=\"Clear the shell screen\"",
        "title": "\"Clear",
        "the": true,
        "shell": true,
        "screen\"": true
      }}>{`SYNOPSYS
        clear
`}</code></pre>
    <hr></hr>
    <h3 {...{
      "id": "exit-or-quit"
    }}><inlineCode parentName="h3">{`exit`}</inlineCode>{` or `}<inlineCode parentName="h3">{`quit`}</inlineCode></h3>
    <p>{`Entering either `}<inlineCode parentName="p">{`exit`}</inlineCode>{` or `}<inlineCode parentName="p">{`quit`}</inlineCode>{` will stop operation of LiveData Migrator when it is run from the command line. All processing will cease, and you will be returned to your system shell.`}</p>
    <p>{`If your LiveData Migrator command line is connected to a LiveData Migrator system service, this command will end your interactive session with that service, which will remain in operation to continue processing Live migrations.`}</p>
    <p>{`If this command is encountered during non-interactive processing of input (such as when you pipe input to an instance as part of another shell script) no further commands contained in that input will be processed.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text",
        "metastring": "title=\"Exit the shell\"",
        "title": "\"Exit",
        "the": true,
        "shell\"": true
      }}>{`SYNOPSYS
        exit

ALSO KNOWN AS
        quit
`}</code></pre>
    <hr></hr>
    <h3 {...{
      "id": "help"
    }}><inlineCode parentName="h3">{`help`}</inlineCode></h3>
    <p>{`Use the `}<inlineCode parentName="p">{`help`}</inlineCode>{` command to get details of all commands available from the action prompt.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text",
        "metastring": "title=\"Display help about available commands\"",
        "title": "\"Display",
        "help": true,
        "about": true,
        "available": true,
        "commands\"": true
      }}>{`SYNOPSYS
        help [[-C] string]

OPTIONS
        -C or --command  string
                The command to obtain help for.
                [Optional, default = <none>]
`}</code></pre>
    <h4 {...{
      "id": "optional-parameters"
    }}>{`Optional Parameters`}</h4>
    <ul>
      <li parentName="ul"><strong parentName="li"><inlineCode parentName="strong">{`--command`}</inlineCode></strong>{`, `}<strong parentName="li"><inlineCode parentName="strong">{`-C`}</inlineCode></strong>{` The command for which help information is wanted`}</li>
    </ul>
    <h4 {...{
      "id": "examples"
    }}>{`Examples`}</h4>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`WANdisco LiveMigrator >> help
AVAILABLE COMMANDS

Built-In Commands
        clear: Clear the shell screen.
        exit, quit: Exit the shell.
        help: Display help about available commands.
        history: Display or save the history of previously run commands
        postprocessors: Display the available post processors
        script: Read and execute commands from a file.
        stacktrace: Display the full stacktrace of the last error.

Exclusion Commands
        exclusion add file-size: Create a new file size rule.
        exclusion add regex: Create a new regex exclusion rule.
        exclusion del: Delete an exclusion rule.
        exclusion list: List all exclusion rules.
        exclusion show: Get details for a particular exclusion rule.

Filesystem Commands
        filesystem add adls2 sharedKey: Add an ADLS2 via HCFS API FileSystem With Shared Key
        filesystem add hdfs: Add an Hadoop HDFS FileSystem
        filesystem add local: Add an Local FileSystem via HCFS FileSystem
        filesystem add s3a: Add an S3A via HCFS API FileSystem.
        filesystem clear: Delete all targets.
        filesystem del: Delete a target.
        filesystem list: List of targets.
        filesystem show: Get target details.
        filesystem types: List the types of target Filesystems available

Migration Commands
        migration abort: Abort a migration.
        migration del: Delete a migration.
        migration exclusion add: Add an exclusion to a migration.
        migration exclusion del: Remove an exclusion from a migration.
        migration list: List running and active migrations.
      * migration new: Create a new migration.
        migration run: Start or resume a migration.
        migration show: Get migration details.
        status: Get migration status.

Source Commands
        source clear: Delete all sources.
        source del: Delete a source.
        source fs show: Show the source FileSystem Configuration

Commands marked with (*) are currently unavailable.
Type \`help <command>\` to learn more.
`}</code></pre>
    <pre><code parentName="pre" {...{
        "className": "language-text"
      }}>{`WANdisco LiveMigrator >> help migration\\ list

NAME
        migration list - List running and active migrations.

SYNOPSYS
        migration list
`}</code></pre>
    <hr></hr>
    <h3 {...{
      "id": "history"
    }}><inlineCode parentName="h3">{`history`}</inlineCode></h3>
    <p>{`Enter `}<inlineCode parentName="p">{`history`}</inlineCode>{` at the action prompt to list all previously entered commands.`}</p>
    <p>{`Entering `}<inlineCode parentName="p">{`history --file <filename>`}</inlineCode>{` will save up to 500 most recently entered commands in text form to the file specified. Use this to record commands that you have executed.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text",
        "metastring": "title=\"Display or save the history of previously run commands\"",
        "title": "\"Display",
        "or": true,
        "save": true,
        "the": true,
        "history": true,
        "of": true,
        "previously": true,
        "run": true,
        "commands\"": true
      }}>{`SYNOPSYS
        history [[--file] file]

OPTIONS
        --file  file
                A file to save history to.
                [Optional, default = <none>]
`}</code></pre>
    <h4 {...{
      "id": "optional-parameters-1"
    }}>{`Optional Parameters`}</h4>
    <ul>
      <li parentName="ul"><strong parentName="li"><inlineCode parentName="strong">{`--file`}</inlineCode></strong>{` The name of the file in which to save the history of commands`}</li>
    </ul>
    <hr></hr>
    <h3 {...{
      "id": "script"
    }}><inlineCode parentName="h3">{`script`}</inlineCode></h3>
    <p>{`Load and execute commands from a text file using the `}<inlineCode parentName="p">{`script --file <filename>`}</inlineCode>{` command. This file should have one command per line, and each will be executed as though they were entered directly at the action prompt in that sequence.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text",
        "metastring": "title=\"Read and execute commands from a file\"",
        "title": "\"Read",
        "and": true,
        "execute": true,
        "commands": true,
        "from": true,
        "a": true,
        "file\"": true
      }}>{`SYNOPSYS
        script [--file] file

OPTIONS
        --file  file
                [Mandatory]
`}</code></pre>
    <h4 {...{
      "id": "mandatory-parameters"
    }}>{`Mandatory Parameters`}</h4>
    <ul>
      <li parentName="ul"><strong parentName="li"><inlineCode parentName="strong">{`--file`}</inlineCode></strong>{` The name of the file containing script commands`}</li>
    </ul>
    <hr></hr>
    <h3 {...{
      "id": "stacktrace"
    }}><inlineCode parentName="h3">{`stacktrace`}</inlineCode></h3>
    <p>{` Use the `}<inlineCode parentName="p">{`stacktrace`}</inlineCode>{` command to get full technical information about the source of an error during LiveData Migrator operation.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text",
        "metastring": "title=\"Display the full stacktrace of the last error\"",
        "title": "\"Display",
        "the": true,
        "full": true,
        "stacktrace": true,
        "of": true,
        "last": true,
        "error\"": true
      }}>{`SYNOPSYS
        stacktrace
`}</code></pre>
    <h2 {...{
      "id": "source-commands"
    }}>{`Source Commands`}</h2>
    <hr></hr>
    <h3 {...{
      "id": "source-clear"
    }}><inlineCode parentName="h3">{`source clear`}</inlineCode></h3>
    <p>{`Clear all information that LiveData Migrator maintains about the source file system by issuing the `}<inlineCode parentName="p">{`source clear`}</inlineCode>{` command. This will allow you to define an alternative source to one previously defined or detected automatically.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text",
        "metastring": "title=\"Delete all sources\"",
        "title": "\"Delete",
        "all": true,
        "sources\"": true
      }}>{`SYNOPSYS
        source clear
`}</code></pre>
    <hr></hr>
    <h3 {...{
      "id": "source-del"
    }}><inlineCode parentName="h3">{`source del`}</inlineCode></h3>
    <p>{`Use `}<inlineCode parentName="p">{`source del`}</inlineCode>{` to delete information about a specific source file system by identifier. You can obtain the identifier for a source file system with the output of the `}<inlineCode parentName="p">{`source fs show`}</inlineCode>{` command.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text",
        "metastring": "title=\"Delete a source\"",
        "title": "\"Delete",
        "a": true,
        "source\"": true
      }}>{`SYNOPSYS
        source del [--file-system-id] string

OPTIONS
        --file-system-id  string

                [Mandatory]
`}</code></pre>
    <h4 {...{
      "id": "mandatory-parameters-1"
    }}>{`Mandatory Parameters`}</h4>
    <ul>
      <li parentName="ul"><strong parentName="li"><inlineCode parentName="strong">{`--file-system-id`}</inlineCode></strong>{` The identifier of the source file system resource to delete`}</li>
    </ul>
    <hr></hr>
    <h3 {...{
      "id": "source-fs-show"
    }}><inlineCode parentName="h3">{`source fs show`}</inlineCode></h3>
    <p>{`Get information about the source file system configuration.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-text",
        "metastring": "title=\"Show the source file system configuration\"",
        "title": "\"Show",
        "the": true,
        "source": true,
        "file": true,
        "system": true,
        "configuration\"": true
      }}>{`SYNOPSYS
        source fs show [--verbose]

OPTIONS
        --verbose
                [Optional, default = false]
`}</code></pre>
    <h4 {...{
      "id": "optional-parameters-2"
    }}>{`Optional Parameters`}</h4>
    <ul>
      <li parentName="ul"><strong parentName="li"><inlineCode parentName="strong">{`--verbose`}</inlineCode></strong>{` Include all configuration properties for the source file system in the response.`}</li>
    </ul>
    <h4 {...{
      "id": "examples-1"
    }}>{`Examples`}</h4>
    <pre><code parentName="pre" {...{}}>{`WANdisco LiveMigrator >> source fs show
[ {
  "fsId" : "auto-discovered-source-hdfs",
  "fsType" : "hdfs",
  "isSource" : true,
  "properties" : {
    "fs.defaultFS" : "hdfs://myhost.localdomain:8020"
  },
  "eventsPosition" : 0
} ]
`}</code></pre>
    <h2 {...{
      "id": "file-system-commands"
    }}>{`File System Commands`}</h2>
    <hr></hr>
    <h3 {...{
      "id": "filesystem-add-adls2-sharedkey"
    }}><inlineCode parentName="h3">{`filesystem add adls2 sharedKey`}</inlineCode></h3>
    <p>{`Add an Azure Data Lake Storage Gen 2 container as a migration target using the `}<inlineCode parentName="p">{`filesystem add adls2-hcfs sharedKey`}</inlineCode>{` command, which requires credentials in the form of an account key.`}</p>
    <pre><code parentName="pre" {...{
        "2": true,
        "className": "language-text",
        "metastring": "title=\"Add an ADLS Gen 2 file system\"",
        "title": "\"Add",
        "an": true,
        "ADLS": true,
        "Gen": true,
        "file": true,
        "system\"": true
      }}>{`SYNOPSYS
        filesystem add adls2 sharedKey [--file-system-id] string
                                       [--storage-account-name] string
                                       [--fs.azure.shared.key] string
                                       [--container.name] string
                                       [--insecure]
                                       [[--properties-file] list]
                                       [[--properties] list]

OPTIONS
        --file-system-id  string

                [Mandatory]

        --storage-account-name  string

                [Mandatory]

        --fs.azure.shared.key  string

                [Mandatory]

        --container.name  string

                [Mandatory]

        --insecure
                [Optional, default = false]

        --properties-file  list
                Load properties from this file
                [Optional, default = <none>]

        --properties  list
                Override properties in comma separated key/value list
                [Optional, default = <none>]
`}</code></pre>
    <h4 {...{
      "id": "mandatory-parameters-2"
    }}>{`Mandatory Parameters`}</h4>
    <ul>
      <li parentName="ul"><strong parentName="li"><inlineCode parentName="strong">{`--file-system-id`}</inlineCode></strong>{` The identifier to give the new file system resource`}</li>
      <li parentName="ul"><strong parentName="li"><inlineCode parentName="strong">{`--storage-account-name`}</inlineCode></strong>{` The name of the ADLS Gen 2 storage account to target`}</li>
      <li parentName="ul"><strong parentName="li"><inlineCode parentName="strong">{`--fs.azure.shared.key`}</inlineCode></strong>{` The shared account key to use as credentials to write to the storage account`}</li>
      <li parentName="ul"><strong parentName="li"><inlineCode parentName="strong">{`--container.name`}</inlineCode></strong>{` The name of the container in the storage account to which content will be migrated`}</li>
    </ul>
    <h4 {...{
      "id": "optional-parameters-3"
    }}>{`Optional Parameters`}</h4>
    <ul>
      <li parentName="ul"><strong parentName="li"><inlineCode parentName="strong">{`--insecure`}</inlineCode></strong>{` When provided, LiveData Migrator will not use TLS to encrypt communication with ADLS Gen 2. This may improve throughput, but should only be used when you have other means of securing communication.`}</li>
      <li parentName="ul"><strong parentName="li"><inlineCode parentName="strong">{`--properties-files`}</inlineCode></strong>{` Reference a list of existing properties files, each that contains Hadoop configuration properties in the format used by `}<inlineCode parentName="li">{`core-site.xml`}</inlineCode>{` or `}<inlineCode parentName="li">{`hdfs-site.xml`}</inlineCode></li>
      <li parentName="ul"><strong parentName="li"><inlineCode parentName="strong">{`--properties`}</inlineCode></strong>{` Specify properties to use in a comma-separated key/value list`}</li>
    </ul>
    <h4 {...{
      "id": "examples-2"
    }}>{`Examples`}</h4>
    <pre><code parentName="pre" {...{}}>{`WANdisco LiveMigrator >> filesystem add adls2 sharedKey --file-system-id mytarget --storage-account-name psmadls2 --container.name lm2target --fs.azure.shared.key Ri5NxHGqoQ79DBGLVn+COK/sRDwbNqAREDACTEDaMxRkvXt2ijUtAkVqVCBj/vaS/NbzR5rtjE2CZ31ejVpUVA==
{
  "fsId" : "lm2target",
  "fsType" : "adls2-hcfs",
  "isSource" : false,
  "properties" : {
    "fsId" : "lm2target",
    "fsType" : "adls2-hcfs",
    "fs.defaultFS" : "abfss://lm2target@psmadls2.dfs.core.windows.net/",
    "fs.azure.account.auth.type.psmadls2.dfs.core.windows.net" : "SharedKey",
    "fs.azure.account.key.psmadls2.dfs.core.windows.net" : "Ri5NxHGqoQ79DBGLVn+COK/sRDwbNqAREDACTEDaMxRkvXt2ijUtAkVqVCBj/vaS/NbzR5rtjE2CZ31ejVpUVA==",
    "fs.abfss.impl" : "org.apache.hadoop.fs.azurebfs.SecureAzureBlobFileSystem",
    "fs.AbstractFileSystem.abfss.impl" : "org.apache.hadoop.fs.azurebfs.Abfss",
    "fs.abfss.impl.disable.cache" : "true"
  },
  "eventsPosition" : 0
}
`}</code></pre>
    <hr></hr>
    <h3 {...{
      "id": "filesystem-add-hdfs"
    }}><inlineCode parentName="h3">{`filesystem add hdfs`}</inlineCode></h3>
    <p>{`Add a Hadoop Distributed File System as either a migration source or target using the `}<inlineCode parentName="p">{`filesystem add hdfs`}</inlineCode>{` command.`}</p>
